import React, { Fragment, useEffect, useState } from "react";

const QuizOver = React.forwardRef((props, ref) => {
  const { levelNames, score, maxQuestion, quizLevel, percent } = props;
  const [asked, setAsked] = useState([]);

  useEffect(() => {
    setAsked(ref.current);
  }, [ref]);

  const averageGrade = maxQuestion / 2;
  const decision =
    score >= averageGrade ? (
      <Fragment>
        <div className="stepsBtnContainer">
          {quizLevel < levelNames.lenght ? (
            <Fragment>
              <p className="successMsg">Bravo, passez au niveau suivant</p>
              <button className="btnResult success">Niveau suivant</button>
            </Fragment>
          ) : (
            <Fragment>
              <p className="successMsg">Bravo vous êtes un expert</p>
              <button className="btnResult gameOver"></button>
            </Fragment>
          )}
        </div>
        <div className="percentage">
          <div className="progressPercent">Réussite : {percent} %</div>
          <div className="progressPercent">
            note : {score}/{maxQuestion}
          </div>
        </div>
      </Fragment>
    ) : (
      <Fragment>
        <div className="stepsBtnContainer">
          <p className="successMsg">Bravo, passez au niveau suivant</p>
        </div>
      </Fragment>
    );

  const questionAnswer = asked.map((question) => {
    return (
      <tr kzy={question.id}>
        <td>{question.question}</td>
        <td>{question.answer}</td>
        <button className="btnInfo">Info</button>
      </tr>
    );
  });

  return (
    <Fragment>
      {decision}
      <hr />
      <p>Les réponses aux questions posées</p>

      <div className="answerContainer">
        <table className="answers">
          <thead>
            <tr>
              <th>Question</th>
              <th>réponse</th>
              <th>infos</th>
            </tr>
          </thead>
          <tbody>{questionAnswer}</tbody>
        </table>
      </div>
    </Fragment>
  );
});

export default React.memo(QuizOver);
