import app from "firebase/app";
import "firebase/auth";
import "firebase/firestore";

const config = {
  apiKey: "AIzaSyDZwGIfY1pe1Vs25vDnXKSqCRxHuggTDZM",
  authDomain: "marvel-quiz-e3014.firebaseapp.com",
  databaseURL: "https://marvel-quiz-e3014.firebaseio.com",
  projectId: "marvel-quiz-e3014",
  storageBucket: "marvel-quiz-e3014.appspot.com",
  messagingSenderId: "286044629490",
  appId: "1:286044629490:web:34536aacedeef1d2dc236b",
};

class Firebase {
  constructor() {
    app.initializeApp(config);
    this.auth = app.auth();
    this.db = app.firestore();
  }

  // inscription
  signupUser = (email, password) =>
    this.auth.createUserWithEmailAndPassword(email, password);

  // Connexion
  loginUser = (email, password) =>
    this.auth.signInWithEmailAndPassword(email, password);

  // Déconnexion
  signoutUser = () => this.auth.signOut();

  // Récupérer le mot de passe
  passwordReset = (email) => this.auth.sendPasswordResetEmail(email);

  // firestore
  user = (uid) => this.db.doc(`users/${uid}`);
}

export default Firebase;
